descriptor.templateCss:
.main-widget {
    display: flex;
    flex-direction: column;
    height: 100%;
    justify-content: center;
    align-items: center;
}

.title, .value {
    padding: 0;
    margin: 0;
    font-family: 'Trebuchet MS', sans-serif;
    line-height: 1;
}

.title {
    font-size: 1.3rem;
    font-weight: 800;
}

.value {
    font-size: 1.5rem;
    font-weight: 700;
    color: #1a73e8;
    margin-top: 10px;
}

descriptor.controllerScript:
self.onInit = function() {
    const ctxScope = self.ctx.$scope;
    const settings = self.ctx.settings;

    ctxScope.title = settings.title || "Title";
    ctxScope.unit = settings.unit || "Unit";
    ctxScope.data = self.ctx.defaultSubscription.data[0];
};

self.onDataUpdated = function() {
    try {
        const rawData = self.ctx.data[0].data[0][1];
        self.ctx.$scope.value = JSON.parse(rawData);
    } catch (error) {
        console.error('Error parsing value', error);
    } finally {
        self.ctx.detectChanges();
    }
};

