descriptor.controllerScript:
self.onInit = function() {
    const { $scope, settings, attributeService, subscriptionApi, controlApi } = self.ctx;

    // Configuración inicial de $scope
    $scope.data = self.ctx.defaultSubscription.data;
    $scope.deviceTitle = settings.deviceTitle || "Device Title";
    $scope.deviceSubtitle = settings.deviceSubtitle || "Device Subtitle";
    $scope.deviceName = settings.deviceName || "device5";
    $scope.entityId = settings.entityId || "cde78cc0-2398-11ef-b19f-cdc6a3f105ff";
    $scope.deviceStatus5 = false;
    $scope.deviceValue5 = 0;
    $scope.rpcMethod = settings.rpcMethod || "set_switch";
    $scope.rpcType = settings.rpcType || "oneway";
    $scope.timeout = settings.requestTimeout || 5000;
    $scope.checkState = false;

    // Inicializar componentes del DOM
    const knob = document.querySelector(".knob");
    const circle = document.getElementById("circle2");
    const pointer = document.querySelector(".pointer");
    const text = document.querySelector(".text");

    let isRotating = false;
    let changeTimeout;

    // Evento de mousedown para iniciar la rotación
    document.addEventListener("mousedown", (e) => {
        if (e.target.closest(".knob")) {
            isRotating = true;
        }
    });

    // Función para rotar el knob
    const rotateKnob = (e) => {
        if (isRotating) {
            const knobX = knob.getBoundingClientRect().left + knob.clientWidth / 2;
            const knobY = knob.getBoundingClientRect().top + knob.clientHeight / 2;

            const deltaX = e.clientX - knobX;
            const deltaY = e.clientY - knobY;

            const angleRad = Math.atan2(deltaY, deltaX);
            const angleDeg = (angleRad * 180) / Math.PI;

            const rotationAngle = (angleDeg - 135 + 360) % 360;

            if (rotationAngle <= 270) {
                pointer.style.transform = `rotate(${rotationAngle - 45}deg)`;

                const progressPercent = rotationAngle / 270;
                circle.style.strokeDashoffset = `${880 - 660 * progressPercent}`;

                const newValue = Math.round(progressPercent * 100);
                text.innerHTML = `${newValue}`;

                clearTimeout(changeTimeout);
                changeTimeout = setTimeout(() => {
                    console.log(`Manual value change: ${newValue}`);
                    $scope.deviceValue5 = newValue;
                    $scope.sendCommand(newValue);
                }, 300);
            }
        }
    };

    // Eventos de mousemove y mouseup
    document.addEventListener("mousemove", rotateKnob);
    document.addEventListener("mouseup", () => { isRotating = false; });

    // Actualización del knob y la barra de progreso
    $scope.updateKnobAndProgressBar = (value) => {
        const startValue = 0; // Volvemos a iniciar desde 0
        const endValue = value;
        const duration = 500;
        let startTime = null;

        const animateValue = (timestamp) => {
            if (!startTime) startTime = timestamp;
            const progress = timestamp - startTime;
            const currentValue = Math.min(startValue + (endValue - startValue) * (progress / duration), endValue);
            const progressPercent = currentValue / 100;
            const rotationAngle = progressPercent * 270;
            pointer.style.transform = `rotate(${rotationAngle - 45}deg)`;
            circle.style.strokeDashoffset = `${880 - 660 * progressPercent}`;
            text.innerHTML = `${Math.round(currentValue)}`;
            if (progress < duration) {
                requestAnimationFrame(animateValue);
            }
        };

        requestAnimationFrame(animateValue);
    };

    // Enviar comando
    $scope.sendCommand = function(status) {

        const rpcParams = { switch: status };
        const isOneWay = $scope.rpcType === "oneway";

        const commandObservable = isOneWay
            ? controlApi.sendOneWayCommand($scope.rpcMethod, rpcParams, $scope.timeout)
            : controlApi.sendTwoWayCommand($scope.rpcMethod, rpcParams, $scope.timeout);

        commandObservable.subscribe(
            (response) => {
                $scope.rpcCommandResponse = isOneWay
                    ? "Command was successfully received by device.<br> No response body because of one way command mode."
                    : "Response from device:<br>" + JSON.stringify(response, undefined, 2);
                console.log($scope.rpcCommandResponse);
                self.ctx.detectChanges();
            },
            (rejection) => {
                $scope.rpcCommandResponse = `Failed to send command to the device:<br>Status: ${rejection.status}<br>Status text: '${rejection.statusText}'`;
                console.log($scope.rpcCommandResponse);
                self.ctx.detectChanges();
            }
        );
    };

    // Cambio de estado del GPIO
    $scope.gpioToggleChange = function($event) {
        attributeService.saveEntityAttributes(
            { entityType: 'DEVICE', id: $scope.entityId },
            'SHARED_SCOPE', [{ key: "deviceState5", value: $event.target.checked }]
        ).subscribe((res) => {
            console.log(res);
        });
    };

    const datasources = [{
        type: "entity",
        dataKeys: [
            { decimals: 0, label: "", name: "deviceState5", settings: {}, type: "attribute" },
            { decimals: 0, label: "", name: "deviceValue5", settings: {}, type: "attribute" }
        ],
        entityFilter: {
            type: "singleEntity",
            singleEntity: {
                id: $scope.entityId,
                entityType: "DEVICE"
            }
        }
    }];

    const subscriptionOptions = {
        type: 'latest',
        datasources: datasources,
        callbacks: {
            onDataUpdated: () => {
                self.onDataUpdated();
            }
        }
    };

    // Crear suscripción
    subscriptionApi.createSubscription(subscriptionOptions, true).subscribe((subscription) => {
        self.ctx.data = subscription.data;
    });
};

self.onDataUpdated = function() {
    const { $scope, data } = self.ctx;

    const parseData = (data, index) => {
        try {
            return JSON.parse(data[index].data[0][1]);
        } catch (e) {
            console.error(`Error parsing data at index ${index}`, e);
            return null;
        }
    };

    const getValue = (data, index) => {
        try {
            return data[index].data[0][1];
        } catch (e) {
            console.error(`Error getting value at index ${index}`, e);
            return null;
        }
    };

    $scope.deviceStatus5 = parseData(data, 0);
    $scope.deviceValue5 = getValue(data, 1);

    if ($scope.deviceValue5 !== null) {
        $scope.updateKnobAndProgressBar($scope.deviceValue5);
    }

    if ($scope.deviceName === 'device5') {
        $scope.checkState = $scope.deviceStatus5;
    }

    self.ctx.detectChanges();
};

