            'device1': 'deviceState1',
            'device2': 'deviceState2',
            'device3': 'deviceState3',
            'device4': 'deviceState4'
        };

        const deviceName = $scope.deviceName;

        if (deviceStateMap.hasOwnProperty(deviceName)) {
            const stateKey = deviceStateMap[deviceName];
            attributeService.saveEntityAttributes(
                { entityType: 'DEVICE', id: $scope.entityId },
                'SHARED_SCOPE', [{ key: stateKey, value: $event.target.checked }]
            ).subscribe((res) => {
                console.log(res);
            });
        }
    };

    // Definición de los datasources
    const datasources = [{
        type: "entity",
        dataKeys: [
            { decimals: 0, label: "", name: "connectivity", settings: {}, type: "attribute" },
            { decimals: 0, label: "", name: "ssid", settings: {}, type: "attribute" },
            { decimals: 0, label: "", name: "rssi", settings: {}, type: "attribute" },
            { decimals: 0, label: "", name: "deviceState1", settings: {}, type: "attribute" },
            { decimals: 0, label: "", name: "deviceState2", settings: {}, type: "attribute" },
            { decimals: 0, label: "", name: "deviceState3", settings: {}, type: "attribute" },
            { decimals: 0, label: "", name: "deviceState4", settings: {}, type: "attribute" }
        ],
        entityFilter: {
            type: "singleEntity",
            singleEntity: {
                id: $scope.entityId,
                entityType: "DEVICE"
            }
        }
    }];

    const subscriptionOptions = {
        type: 'latest',
        datasources: datasources,
        callbacks: {
            onDataUpdated: () => {
                self.onDataUpdated();
            }
        }
    };

    // Creación de la suscripción
    subscriptionApi.createSubscription(subscriptionOptions, true).subscribe((subscription) => {
        self.ctx.defaultSubscription = subscription;
        self.ctx.data = subscription.data;
        self.ctx.datasources = subscription.datasources;
        $scope.data = self.ctx.data;
    });
};


self.onDataUpdated = function() {
    const { $scope, data } = self.ctx;

    const parseJSON = (data, index) => {
        try {
            return JSON.parse(data[index].data[0][1]);
        } catch (e) {
            console.error(`Error parsing data at index ${index}`, e);
            return null;
        }
    };

    const parseString = (data, index) => {
        try {
            return data[index].data[0][1];
        } catch (e) {
            console.error(`Error parsing string at index ${index}`, e);
            return null;
        }
    };

    $scope.statusDevice = parseJSON(data, 0);
    $scope.ssid = parseString(data, 1);
    $scope.rssi = parseJSON(data, 2);
    $scope.deviceStatus1 = parseJSON(data, 3);
    $scope.deviceStatus2 = parseJSON(data, 4);
    $scope.deviceStatus3 = parseJSON(data, 5);
    $scope.deviceStatus4 = parseJSON(data, 6);

    const deviceStatuses = {
        'device1': $scope.deviceStatus1,
        'device2': $scope.deviceStatus2,
        'device3': $scope.deviceStatus3,
        'device4': $scope.deviceStatus4
    };

    if (deviceStatuses[$scope.deviceName] !== undefined) {
        console.log($scope.deviceName);
        $scope.checkState = deviceStatuses[$scope.deviceName];
    }

    $scope.wifiStatus = `SSID: ${$scope.ssid} / RSSI: ${$scope.rssi}`;
    self.ctx.detectChanges();
};

